import React, { useEffect, useState } from "react";

function ProfileEdit() {
  const [user, setUser] = useState({});

  // This is an old version, better to use async-await
  // useEffect(() => {
  //   fetch("https://jsonplaceholder.typicode.com/users/1")
  //     .then((response) => response.json())
  //     .then(setUser);
  // }, []);

  useEffect(() => {
    async function loadUsers() {
      const response = await fetch(
        "https://jsonplaceholder.typicode.com/users/1"
      );
      const userFromAPI = await response.json();
      setUser(userFromAPI);
    }
    loadUsers();
  }, []); // Passing [] so that it only runs the effect once

  const changeHandler = event => {
    setUser({ ...user, [event.target.name]: event.target.value })
  }

  const submitHandler = async (event) => {
    event.preventDefault();
    const response = await fetch(
      `https://jsonplaceholder.typicode.com/users/${user.id}`,
      {
        method: "PUT",
        body: JSON.stringify(user),
        headers: {
          "Content-type": "application/json;charset=UTF-8"
        }
      }
    );
    const savedData = await response.json();
    console.log("Saved user!", savedData);
  };


  if (user.id) {
    // `user.id` is truthy after the API call returns
    return (
      <form name="profileEdit" onsubmit={submitHandler}>
        <div>
          <label htmlFor="username">User Name:</label>
          <input
            id="username"
            name="username"
            type="text"
            required={true}
            value={user.username}
            onChange={changeHandler}
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            name="email"
            type="email"
            required={true}
            value={user.email}
            onChange={changeHandler}
          />
        </div>
        <button type='submit'>Save</button>
      </form>
    );
  }
  return "Loading...";
}

export default ProfileEdit;
