import React, { useState } from "react";
import Content from "./Content";
import Header from "./Header";

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const toggleLoggedIn = () => setLoggedIn(!loggedIn);

  const [fontSize, setFontSize] = useState(12);
  const handleFontSizeIncrease = () => {(fontSize) => setFontSize(fontSize + 2)}

  function IncreaseFontButton() {
    return <button>Increase Font Size</button>
  }

  return (
    <div>
      <Header loggedIn={loggedIn} handleLoggedInClick={toggleLoggedIn} />
      <IncreaseFontButton onClick={handleFontSizeIncrease}/>
      <Content loggedIn={loggedIn} fontSize={setFontSize}/>
    </div>
  );
}

export default App;
