import React from "react";

function Clock() {

  const hours = new Date().getHours();
  hours = hours + 5;
  switch(true) {
    case (hours < 12):
      return <>Good Morning!</>;
    case (12 <= hours && hours <= 18):
      return <>Good Afternoon!</>;
    case (hours > 18):
      return <>Good Evening!</>;
  }
}

export default Clock;
